import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    navigate('/');
  };

  const getDashboardLink = () => {
    if (!user) return null;
    
    switch (user.role) {
      case 'USER':
        return '/user/dashboard';
      case 'VENDOR':
        return '/vendor/dashboard';
      case 'ADMIN':
        return '/admin/dashboard';
      default:
        return null;
    }
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            BridgeTheGap
          </Link>
          
          <ul className="navbar-nav">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/search">Search</Link></li>
            
            {user ? (
              <>
                <li><Link to={getDashboardLink()}>Dashboard</Link></li>
                <li>
                  <span className="user-info">
                    Welcome, {user.name} ({user.role})
                  </span>
                </li>
                <li>
                  <button className="btn btn-outline" onClick={handleLogout}>
                    Logout
                  </button>
                </li>
              </>
            ) : (
              <>
                <li><Link to="/user/login" className="btn btn-outline">User Login</Link></li>
                <li><Link to="/vendor/login" className="btn btn-outline">Vendor Login</Link></li>
                <li><Link to="/admin/login" className="btn btn-primary">Admin Login</Link></li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
